import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { UsersService } from 'src/app/services/users.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  constructor(private userService: UsersService, private router: Router) {}

  userForm = new FormGroup({
    userName: new FormControl('', [Validators.required]),
    password: new FormControl('', [Validators.required]),
  });
  userName: string = '';
  password: string = '';
  formDone(e: Event) {
    this.userService.getAllUsers().subscribe((response) => {
      const user = response.find((a: any) => {
        return a.userName === this.userName && a.password === this.password;
      });

      if (user) {
        this.userService.UserId?.next(user.id);
        this.userService.login(this.userName, this.password);
        this.router.navigate(['/Home']);

      } else {
        this.userForm.reset();
        alert('Invalid Username Or Password!');
      }
    });
  }
}
<div class="container">
  <div class="row justify-content-center mt-5">
    <div class="col-md-8">
      <div class="card">
        <div class="card-header header">
          <h4 class="fs-2 fw-bold">Sign In</h4>
        </div>
        <div class="card-body">
          <form [formGroup]="userForm" (submit)="formDone($event)">
            <div class="form-group">
              <label for="username">Username</label>
              <input
                [(ngModel)]="userName"
                type="text"
                class="form-control"
                formControlName="userName"
                aria-describedby="userNameHelp"
                id="username"
                placeholder="Enter username"
              />
            </div>
            <div class="form-group">
              <label for="password">Password</label>
              <input
                [(ngModel)]="password"
                type="password"
                class="form-control"
                formControlName="password"
                id="password"
                placeholder="Enter password"
              />
            </div>
            <button type="submit" class="btn mt-4 px-3">Login</button>
            <button class="btn mt-4 px-3 mx-3" [routerLink]="['/Register']">
              Register
            </button>
          </form>
        </div>
      </div>
    </div>
  </div>
</div>
